import React, { useState, useEffect, useRef } from "react";
import "./index.css";

const phases = [
  { name: "Inhale", duration: 4 },
  { name: "Hold", duration: 7 },
  { name: "Exhale", duration: 8 },
];

function getTodayKey() {
  return new Date().toLocaleDateString("en-CA", { timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone });
}

function updateLocalStorage(minutes) {
  const key = getTodayKey();
  const data = JSON.parse(localStorage.getItem("breathingHistory") || "{}");
  data[key] = (data[key] || 0) + minutes;
  localStorage.setItem("breathingHistory", JSON.stringify(data));
}

function fadeOutAudio(audioRef, duration = 1000) {
  const audio = audioRef.current;
  const step = 0.05;
  const fadeInterval = duration / (1 / step);

  const fade = setInterval(() => {
    if (audio.volume > step) {
      audio.volume -= step;
    } else {
      audio.volume = 1;
      audio.pause();
      audio.currentTime = 0;
      clearInterval(fade);
    }
  }, fadeInterval);
}

export default function BreathingApp() {
  const [isRunning, setIsRunning] = useState(false);
  const [meditationTime, setMeditationTime] = useState(1); // in minutes
  const [elapsed, setElapsed] = useState(0);
  const [phaseIndex, setPhaseIndex] = useState(0);
  const [phaseTimeLeft, setPhaseTimeLeft] = useState(phases[0].duration);
  const [sessionStarted, setSessionStarted] = useState(false);
  const [sessionComplete, setSessionComplete] = useState(false);
  const intervalRef = useRef(null);

  const inhaleAudio = useRef(new Audio("/inhale.mp3"));
  const exhaleAudio = useRef(new Audio("/exhale.mp3"));

  const stopAllSounds = () => {
    fadeOutAudio(inhaleAudio);
    fadeOutAudio(exhaleAudio);
  };

  const playAudioForPhase = async (phaseName) => {
    try {
      if (phaseName === "Inhale") {
        inhaleAudio.current.pause();
        inhaleAudio.current.currentTime = 0;
        inhaleAudio.current.volume = 1;
        await inhaleAudio.current.play();
      } else if (phaseName === "Exhale") {
        exhaleAudio.current.pause();
        exhaleAudio.current.currentTime = 0;
        exhaleAudio.current.volume = 1;
        await exhaleAudio.current.play();
      }
    } catch (e) {
      console.warn("Audio play interrupted:", e);
    }
  };

  useEffect(() => {
    if (isRunning && !sessionComplete) {
      intervalRef.current = setInterval(() => {
        setPhaseTimeLeft((prev) => {
          if (prev > 1) return prev - 1;

          setPhaseIndex((prevIndex) => {
            const nextIndex = (prevIndex + 1) % phases.length;
            const nextPhase = phases[nextIndex];
            setPhaseTimeLeft(nextPhase.duration);

            stopAllSounds();
            playAudioForPhase(nextPhase.name);

            if (nextIndex === 0) {
              setElapsed((prevElapsed) => {
                const nextElapsed = prevElapsed + phases.reduce((a, b) => a + b.duration, 0);
                if (nextElapsed >= meditationTime * 60) {
                  setIsRunning(false);
                  setSessionComplete(true);
                  updateLocalStorage(meditationTime);
                  stopAllSounds();
                }
                return nextElapsed;
              });
            }

            return nextIndex;
          });

          return 0;
        });
      }, 1000);
    }

    return () => clearInterval(intervalRef.current);
  }, [isRunning, meditationTime, sessionComplete]);

  const startMeditation = () => {
    stopAllSounds();
    setElapsed(0);
    setPhaseIndex(0);
    setPhaseTimeLeft(phases[0].duration);
    setIsRunning(true);
    setSessionStarted(true);
    setSessionComplete(false);
    playAudioForPhase("Inhale");
  };

  const togglePause = () => {
    setIsRunning((prev) => !prev);
  };

  const todayKey = getTodayKey();
  const history = JSON.parse(localStorage.getItem("breathingHistory") || "{}");
  const todayMinutes = history[todayKey] || 0;

  const timeRemaining = Math.max(meditationTime * 60 - elapsed, 0);

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-900 text-white px-4 py-10 font-sans">
      <h1 className="text-4xl font-extrabold mb-2 tracking-tight">Time to Get Sleepy</h1>
      <p className="mb-6 text-sm text-gray-400">Todayâ€™s total: {todayMinutes} minute(s)</p>

      <div className="mb-6 w-full max-w-xs">
        <label className="block mb-2 text-lg text-center font-medium">Duration (minutes)</label>
        <input
          type="number"
          value={meditationTime}
          onChange={(e) => setMeditationTime(Number(e.target.value))}
          min={1}
          className="w-full p-3 rounded-xl bg-black text-white text-xl border border-gray-600 focus:outline-none focus:ring-2 focus:ring-blue-400"
        />
      </div>

      {!isRunning && !sessionStarted && (
        <button
          className="bg-blue-600 hover:bg-blue-700 px-8 py-3 rounded-full text-xl font-semibold shadow-lg transition-transform transform hover:scale-105"
          onClick={startMeditation}
        >
          Start
        </button>
      )}

      {isRunning && (
        <div className="text-center mt-6">
          <p className="text-2xl mb-2 font-semibold tracking-wide">{phases[phaseIndex].name}</p>
          <p className="text-6xl font-bold mb-4 tracking-tight">{phaseTimeLeft}s</p>
          <p className="text-sm mb-4 text-gray-300">
            Time remaining: {Math.floor(timeRemaining / 60)}:{(timeRemaining % 60).toString().padStart(2, "0")}
          </p>
          <button
            className="bg-yellow-500 hover:bg-yellow-600 px-6 py-2 rounded-full text-lg font-medium text-gray-900 transition-transform transform hover:scale-105"
            onClick={togglePause}
          >
            {isRunning ? "Pause" : "Resume"}
          </button>
        </div>
      )}

      {sessionComplete && (
        <div className="text-center mt-6">
          <p className="text-2xl font-medium mb-4">Session complete. ðŸ§˜</p>
          <button
            className="bg-blue-600 hover:bg-blue-700 px-8 py-3 rounded-full text-xl font-semibold shadow-md transition-transform transform hover:scale-105"
            onClick={startMeditation}
          >
            Start Another Session
          </button>
        </div>
      )}
    </div>
  );
}
