import React, { useState, useEffect, useRef } from "react";

const phases = [
  { name: "Inhale", duration: 4 },
  { name: "Hold", duration: 7 },
  { name: "Exhale", duration: 8 },
];

function getTodayKey() {
  return new Date().toLocaleDateString("en-CA", { timeZone: Intl.DateTimeFormat().resolvedOptions().timeZone });
}

function updateLocalStorage(minutes) {
  const key = getTodayKey();
  const data = JSON.parse(localStorage.getItem("breathingHistory") || "{}");
  data[key] = (data[key] || 0) + minutes;
  localStorage.setItem("breathingHistory", JSON.stringify(data));
}

function fadeOutAudio(audioRef, duration = 1000) {
  const audio = audioRef.current;
  const step = 0.05;
  const fadeInterval = duration / (1 / step);

  const fade = setInterval(() => {
    if (audio.volume > step) {
      audio.volume -= step;
    } else {
      audio.volume = 1;
      audio.pause();
      audio.currentTime = 0;
      clearInterval(fade);
    }
  }, fadeInterval);
}

export default function BreathingApp() {
  const [isRunning, setIsRunning] = useState(false);
  const [meditationTime, setMeditationTime] = useState(1); // minutes
  const [elapsed, setElapsed] = useState(0);
  const [phaseIndex, setPhaseIndex] = useState(0);
  const [phaseTimeLeft, setPhaseTimeLeft] = useState(phases[0].duration);
  const [sessionStarted, setSessionStarted] = useState(false);
  const [sessionComplete, setSessionComplete] = useState(false);
  const intervalRef = useRef(null);

  const inhaleAudio = useRef(new Audio("/inhale.mp3"));
  const exhaleAudio = useRef(new Audio("/exhale.mp3"));

  const stopAllSounds = () => {
    fadeOutAudio(inhaleAudio);
    fadeOutAudio(exhaleAudio);
  };

  useEffect(() => {
    if (isRunning && !sessionComplete) {
      intervalRef.current = setInterval(() => {
        setPhaseTimeLeft((prev) => {
          if (prev > 1) return prev - 1;

          setPhaseIndex((prevIndex) => {
            const nextIndex = (prevIndex + 1) % phases.length;
            const nextPhase = phases[nextIndex];
            setPhaseTimeLeft(nextPhase.duration);

            stopAllSounds();
            if (nextPhase.name === "Inhale") inhaleAudio.current.play();
            if (nextPhase.name === "Exhale") exhaleAudio.current.play();

            // Track elapsed time on each full cycle
            if (nextIndex === 0) {
              setElapsed((prevElapsed) => {
                const nextElapsed = prevElapsed + phases.reduce((a, b) => a + b.duration, 0);
                if (nextElapsed >= meditationTime * 60) {
                  setIsRunning(false);
                  setSessionComplete(true);
                  updateLocalStorage(meditationTime);
                  stopAllSounds();
                }
                return nextElapsed;
              });
            }

            return nextIndex;
          });

          return 0;
        });
      }, 1000);
    }

    return () => clearInterval(intervalRef.current);
  }, [isRunning, meditationTime, sessionComplete]);

  const startMeditation = () => {
    stopAllSounds();
    setElapsed(0);
    setPhaseIndex(0);
    setPhaseTimeLeft(phases[0].duration);
    setIsRunning(true);
    setSessionStarted(true);
    setSessionComplete(false);
    inhaleAudio.current.volume = 1;
    inhaleAudio.current.play();
  };

  const togglePause = () => {
    setIsRunning((prev) => !prev);
  };

  const todayKey = getTodayKey();
  const history = JSON.parse(localStorage.getItem("breathingHistory") || "{}");
  const todayMinutes = history[todayKey] || 0;

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gray-900 text-white p-4">
      <h1 className="text-3xl font-bold mb-2">Time to Get Sleepy</h1>
      <p className="mb-6 text-sm text-gray-400">Today’s total: {todayMinutes} minute(s)</p>

      <div className="mb-6 w-full max-w-xs">
        <label className="block mb-2 text-lg text-center">Duration (minutes)</label>
        <input
          type="number"
          value={meditationTime}
          onChange={(e) => setMeditationTime(Number(e.target.value))}
          min={1}
          className="w-full p-3 rounded bg-black text-white text-xl"
        />
      </div>

      {!isRunning && !sessionStarted && (
        <button
          className="bg-blue-600 hover:bg-blue-700 px-6 py-3 rounded-xl text-xl"
          onClick={startMeditation}
        >
          Start
        </button>
      )}

      {isRunning && (
        <div className="text-center">
          <p className="text-2xl mb-2">{phases[phaseIndex].name}</p>
          <p className="text-5xl font-bold mb-4">{phaseTimeLeft}s</p>
          <p className="text-sm mb-4">
            Time elapsed: {Math.floor(elapsed / 60)}:{(elapsed % 60).toString().padStart(2, "0")}
          </p>
          <button
            className="bg-yellow-500 hover:bg-yellow-600 px-4 py-2 rounded-xl text-lg"
            onClick={togglePause}
          >
            {isRunning ? "Pause" : "Resume"}
          </button>
        </div>
      )}

      {sessionComplete && (
        <>
          <p className="text-2xl mt-4">Session complete. 🧘</p>
          <button
            className="mt-4 bg-blue-600 hover:bg-blue-700 px-6 py-3 rounded-xl text-xl"
            onClick={startMeditation}
          >
            Start Another Session
          </button>
        </>
      )}
    </div>
  );
}
